class Controller:
    def __init__(this, ui):
        this.intranetStatus = False #nincs kapcsolat
        this.intranetErrorCounter = 0 #nincs hiba
        this.s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        this.gui = ui
        this.indicators = Indicators()
        this.controls = Controls()
        this.gui.SetUpController(this)
        this.toggle = True
        this.toggleState = 0
        
    def Control(this):
        this.gui.UpdateUI(this.toggle)
        this.intranetExchange()
        
        this.toggleState = this.toggleState + 1
        if this.toggleState > 5:
            this.toggleState = 0
            this.toggle = not this.toggle #toggle value
        
    def toggleConnection(this):
        if not this.intranetStatus: #meg nincs kapcsolodva => kapcsolodas
            try:
                this.s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
                this.s.connect(('localhost', PORT))
                this.s.settimeout(TIMEOUT)
            except:
                print('[ImreERR] Nem sikerult csatlakozni a szerverhez')
                #print(sys.exc_info())
                try:
                    this.s.close()
                except:
                    print('[ImreERR] A socketet nem lehet ervenyteleniteni')
                #this.indicators.Kapcsolat = 0
                return 1
            print('[ImreApp] Kapcsolodva a szerverhez')
            this.indicators.Kapcsolat = 2
            this.intranetStatus = True
        else: #mar kapcsolodva van => lekapcsolodas
            try:
                this.s.close()
            except:
                print('[ImreERR] Nem lehet lecsatlakozni a szerverrol')
                this.indicators.Kapcsolat = 0
                return 1
            print('[ImreApp] Lekapcsolva a szerverrol')
            this.indicators.Kapcsolat = 0 #Lekapcsolva
            this.intranetStatus = False
            return 0
            
    def intranetExchange(this):
        while True:
            if not this.intranetStatus: #Nincs kapcsolodva
                return 0
            try:
                this.s.send(pickle.dumps(Controls()))
                msg_in = this.s.recv(2048)
            except:
                print('[ImreERR] Hibas adatatvitel')
                this.intranetErrorCounter = this.intranetErrorCounter + 1
                if this.intranetErrorCounter > PROBALKOZASOK: #Nem lehet megoldani a hibat
                    this.toggleConnection() #Lekapcsolodas
                return 1
            try:
                data_in = pickle.loads(msg_in)
            except:
                print('[ImreErr] Ertelmezhetetlen bejovo adat')
                return 2
            temp = [this.indicators.Kapcsolat, this.indicators.Felulirva]
            if isinstance(data_in, Indicators):
                this.indicators = data_in
            this.indicators.Kapcsolat = temp[0]
            #this.indicators.Felulirva = temp[1]
            #return 0
        
    


